<?php session_start(); if(!isset($_SESSION['admin_id'])) header('Location: /user/login.php'); require_once __DIR__.'/../config/db.php'; $err=''; if($_SERVER['REQUEST_METHOD']==='POST'){ $name=trim($_POST['name']); $cat=trim($_POST['category']); $price=floatval($_POST['price']); $desc=trim($_POST['description']); $img='assets/images/placeholder-dish.jpg'; $ins=$conn->prepare('INSERT INTO menu_items (name,category,price,description,image) VALUES (?,?,?,?,?)'); $ins->bind_param('ssdsd',$name,$cat,$price,$desc,$img); if($ins->execute()) header('Location: manage_menu.php'); else $err='DB error'; } include_once __DIR__.'/../includes/header.php'; ?><div class='card p-3'><h3>Add Dish</h3><?php if($err) echo '<div class="alert alert-danger">'.htmlspecialchars($err).'</div>'; ?><form method='post'><input class='form-control mb-2' name='name' placeholder='Name' required><input class='form-control mb-2' name='category' placeholder='Category' required><input class='form-control mb-2' name='price' type='number' step='0.01' placeholder='Price' required><textarea class='form-control mb-2' name='description' placeholder='Description'></textarea><button class='btn btn-primary'>Add</button></form></div><?php include_once __DIR__.'/../includes/admin_footer.php'; ?>