<?php session_start(); require_once __DIR__.'/../config/db.php'; if($_SERVER['REQUEST_METHOD']==='POST'){ $name=trim($_POST['name']); $email=trim($_POST['email']); $pwd=$_POST['password']; $hash=password_hash($pwd,PASSWORD_DEFAULT); $ins=$conn->prepare('INSERT INTO users (name,email,password,role) VALUES (?,?,?,"user")'); $ins->bind_param('sss',$name,$email,$hash); $ins->execute(); $_SESSION['user_id']=$ins->insert_id; $_SESSION['user_name']=$name; $_SESSION['role']='user'; header('Location: menu.php'); exit; } include_once __DIR__.'/../includes/header.php'; ?><div class='row justify-content-center'><div class='col-md-6'><div class='card p-4'><h3>Register</h3><form method='post'><input class='form-control mb-2' name='name' placeholder='Name' required><input class='form-control mb-2' name='email' placeholder='Email' type='email' required><input class='form-control mb-2' name='password' placeholder='Password' type='password' required><button class='btn btn-primary'>Register</button></form></div></div></div><?php include_once __DIR__.'/../includes/footer.php'; ?>